import React from 'react';
import { useQuery } from '@apollo/react-hooks';
import gql from 'graphql-tag';

const Home = () => {
  const { loading, data: { getPosts } } = useQuery(FETCH_POST_QUERY);
  if (loading) {
    return <div>
      Loading...
    </div>;
  }

  return (
    <div>
      {
        getPosts.map(({ body, username }) => <div>{body} {username}</div>)
      }
    </div>
  )
}

const FETCH_POST_QUERY = gql`{
  getPosts{
    id body createdAt username likeCount
    likes{
      username
    }
    commentCount
    comments{
      id username createdAt body
    }
  }
}`;

export default Home;
