import React, { useState } from 'react';
import { Form, Button } from 'semantic-ui-react';
import { useMutation } from '@apollo/react-hooks';
import gql from 'graphql-tag';

const Register = () => {
  const [errors, setErrors] = useState({});
  const [values, setValues] = useState({
    username: '',
    password: '',
    confirmPassword: '',
    email: ''
  });

  const onChange = event => {
    setValues({
      ...values,
      [event.target.name]: event.target.value
    });
  };

  const [addUser, { loading }] = useMutation(REGISTER_USER, {
    update(proxy, result) {//this will be triggered if the mutation is successfully executed
      console.log(result);
    },
    onError(err) {
      console.log(err.graphQLErrors[0].extensions.exception.errors);
      setErrors(err.graphQLErrors[0].extensions.exception.errors);
    },
    variables: values
  });

  const onSubmit = event => {
    event.preventDefault();
    // if (values.username && values.password && values.email) {
      addUser();//esto llama a addUser, que a su vez usa REGISTER_USER, que es igual a: 
    // }
    {/**
      mutation {
        register(registerInput:{
          username: "lala"
          password: "12345678"
          confirmPassword: "12345678"
          email: "lala@gmail.com"
        }) {
          id
          email
          token
          username
          createdAt
        }
      }
    */}
  }

  return <div className="form-container">
    <Form onSubmit={onSubmit} noValidate className={loading ? "loading" : ""}>
      <h1>
        Register
      </h1>
      <Form.Input
        label="Username" 
        placeholder="John Doe" 
        name="username" 
        value={values.username}
        type="text"
        onChange={onChange} />
      <Form.Input
        label="E-mail" 
        type="email"
        placeholder="johndoe@pipmail.com" 
        name="email"
        value={values.email}
        onChange={onChange} />
      <Form.Input
        label="Password" 
        type="password" 
        placeholder="Jhon1234" 
        name="password" 
        value={values.password}
        onChange={onChange} />
      <Form.Input
        label="Confirm Password" 
        type="password" 
        placeholder="Jhon1234" 
        name="confirmPassword"
        value={values.confirmPassword}
        onChange={onChange} />

        <Button type="submit" primary>
          Register
        </Button>
    </Form>
  </div>;
}

const REGISTER_USER = gql`
mutation register (
  $username: String!
  $email: String!
  $password: String!
  $confirmPassword: String!
  ) {
    register(
      registerInput: {
        username: $username
        email: $email
        password: $password
        confirmPassword: $confirmPassword
      }
    ) {
      id email username createdAt token
    }
  }
`;

export default Register;
